import axios from 'axios'

const USER_API_URL = 'http://localhost:8080'
const INSTRUCTOR_API_URL = `${USER_API_URL}`

class SalaryDataService {

    retrieveAllSalaries() {
        return axios.get(`${INSTRUCTOR_API_URL}/salaries`, {
            headers: {
              "Access-Control-Allow-Origin": "*"
            }});
    }

    deleteSalary(id) {
        return axios.delete(`${INSTRUCTOR_API_URL}/salaries/${id}`);
    }

    retrieveSalary(id) {
        return axios.get(`${INSTRUCTOR_API_URL}/salaries/${id}`);
    }

    updateSalary(id, salary) {
        return axios.put(`${INSTRUCTOR_API_URL}/salaries/${id}`, salary);
    }
  
    createSalary(salary) {
        return axios.post(`${INSTRUCTOR_API_URL}/salaries`, salary);
    }
}


export default new SalaryDataService()
