import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import UserDataService from '../service/UserDataService';
import photo from '../resources/getimg.png'

class UserDetailsComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            name: '',
            surname: '',
            age: '',
            email: '',
            salary: '',
            status: '',
            education: '',
            educationPlace: ''
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
        // this.getPremiya = this.getPremiya.bind(this)
    }

    componentDidMount() {

        console.log(this.state.id)

        UserDataService.retrieveUser(this.state.id)
            .then(response => this.setState({
                name: response.data.name,
                surname: response.data.surname,
                age: response.data.age,
                email: response.data.email,
                salary: response.data.salary,
                status: response.data.status,
                education: response.data.education,
                educationPlace: response.data.educationPlace
            }))

    }

    onSubmit(values) {
        let user = {
            id: this.state.id,
            name: values.name,
            surname: values.surname,
            age: values.age,
            email: values.email,
            salary: values.salary,
            status: values.status,
            education: values.education,
            educationPlace: values.educationPlace
        }


        if (values.id == -1) {
            UserDataService.createUser(user)
                .then(() => this.props.history.push('/users'))
        } else {
            UserDataService.updateUser(this.state.id, user)
                .then(() => this.props.history.push('/users'))
        }

        console.log(values);
    }

    validate(values) {
        let errors = {}
        if (!values.name) {
            errors.name = 'Введите имя'
        } else if (values.name.length < 2) {
            errors.name = 'Имя должно состоять минимум из 2-х букв'
        }
        if (!values.surname) {
            errors.surname = 'Введите фамилию'
        } else if (values.surname.length < 3) {
            errors.surname = 'Фамилия должно состоять минимум из 2-х букв'
        }
        if (!values.age) {
            errors.age = 'Введите возраст'
        }
        if (!values.email) {
            errors.email = 'Введите адрес электронной почты'
        } else if (values.email.length < 8) {
            errors.email = 'Имя должно состоять минимум из 8-ми символов'
        }

        return errors
    }

    // getPremiya(){
    //     var oklad = document.getElementById("materialRegisterFormFirstName").value;
    //     var okladInt = parseInt(oklad, 10);
    //     var premiya = okladInt * 0.15;
    //     var sum = okladInt + premiya;

    //     document.getElementById("materialRegisterFormLastName").value = premiya;
    //     document.getElementById("materialRegisterFormPhone").value = sum;
    // }

    render() {
        let { name, surname, id, age, email, salary, status, education, educationPlace } = this.state

        return (
            <div>
                <h3>Пользователь</h3>
                <header className="header">
                    <div className="container">
                        <div className="teacher-name">

                            <div className="row" >
                                <div className="col-md-9">
                                    <h2 ><strong>{this.state.name} {this.state.surname}</strong></h2>
                                </div>
                                <div className="col-md-3">
                                    {/* <div class="button" >
                                        <a href="#" class="btn btn-outline-success btn-sm">Edit Profile</a>
                                    </div> */}
                                </div>
                            </div>
                        </div>

                        <div className="row" >
                            <div className="col-md-3">
                                <a href="#"> <img className="rounded-circle" src={photo} alt="Photo" /></a>
                            </div>

                            <div className="col-md-6">
                                <p>Email: <strong>{this.state.email}</strong></p>
                                <p>Возраст: <strong>{this.state.age}</strong></p>
                                <p>Классификация: <strong>{this.state.status}</strong></p>
                                <p>Оклад: <strong>{this.state.salary}</strong></p>
                                <p>Образование: <strong>{this.state.education}</strong></p>
                                <p>Место получения образования: <strong>{this.state.educationPlace}</strong></p>
                            </div>

                            {/* <div className="col-md-3 text-center">
                                <span className="number" >E-mail:<strong>{this.state.email}</strong></span>
                                <div class="button" >
                                    <a href="mailto:ahmkctg@yahoo.com" class="btn btn-outline-success btn-block">Send Email</a>
                                </div>
                            </div> */}
                        </div>
                    </div>
                </header>
                <div class="card-body px-lg-5 pt-0">
                <Formik
                    initialValues={{ id, name, surname, age, email }}
                    onSubmit={this.onSubmit}
                    validateOnChange={false}
                    validateOnBlur={false}
                    validate={this.validate}
                    enableReinitialize={true}
                >
                    {
                        (props) => (
                            <Form class="text-center">

                            <div class="form-row">
                                <div class="col">
                                    <div class="md-form">
                                        <input type="text" id="materialRegisterFormFirstName" class="form-control" />
                                        <label for="materialRegisterFormFirstName">Оклад</label>
                                    </div>
                                </div>
                                <div class="col">
                                    <div class="md-form">
                                        <input type="text" id="materialRegisterFormLastName" class="form-control" disabled/>
                                        <label for="materialRegisterFormLastName">Премия</label>
                                    </div>
                                </div>
                            </div>
                
                            <div class="md-form mt-0">
                                <input type="text" id="materialRegisterFormEmail" class="form-control" />
                                <label for="materialRegisterFormEmail">Месяц</label>
                            </div>
                
                            <div class="md-form">
                                <input type="text" id="materialRegisterFormPassword" class="form-control" aria-describedby="materialRegisterFormPasswordHelpBlock" />
                                <label for="materialRegisterFormPassword">Тип з/п</label>
                                <small id="materialRegisterFormPasswordHelpBlock" class="form-text text-muted mb-4">
                                    Минимум 5 символов
                                </small>
                            </div>
                
                            <div class="md-form">
                                <input type="text" id="materialRegisterFormPhone" class="form-control" aria-describedby="materialRegisterFormPhoneHelpBlock" disabled/>
                                <label for="materialRegisterFormPhone">Суммарно</label>
                            </div>
                
                            <button class="btn btn-outline-info btn-rounded btn-block my-4 waves-effect z-depth-0" onClick={() => this.getPremiya()}>Рассчитать</button>
                            <button class="btn btn-outline-info btn-rounded btn-block my-4 waves-effect z-depth-0" type="submit">Добавить</button>
                        </Form>
                        )
                    }
                </Formik>
                </div>
            </div>
        )
    }
}

export default UserDetailsComponent
