import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import WorkAmountDataService from '../service/WorkAmountDataService';
import UserDataService from '../service/UserDataService';

class WorkAmountComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            workdays: '',
            totalWorkdays: '',
            assessment: '',
            month: '',
            year: '',
            user: '',
            users: []
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
        this.handleDropdownChange = this.handleDropdownChange.bind(this)
    }

    componentDidMount() {

        console.log(this.state.id)

        this.getUsers();

        // eslint-disable-next-line
        if (this.state.id == -1) {
            return
        }

        WorkAmountDataService.retrieveAmount(this.state.id)
            .then(response => this.setState({
                percent: response.data.percent,
                index: response.data.index,
                coefficient: response.data.coefficient,
                month: response.data.month,
                user: response.data.user
            }))

    }

    onSubmit(values) {
        let setUser = this.getUser(values.user);
        let workAmount = {
            id: this.state.id,
            percent: values.percent,
            index: values.index,
            coefficient: values.coefficient,
            month: values.month 
        }


        if (values.id == -1) {
            console.log(workAmount);
            WorkAmountDataService.createAmount(workAmount, values.user)
                .then(() => this.props.history.push('/workamounts'))
        } else {
            WorkAmountDataService.updateAmount(this.state.id, workAmount)
                .then(() => this.props.history.push('/workamounts'))
        }

        console.log(values);
    }

    validate(values) {
        let errors = {}
        if (!values.percent) {
            errors.percent = 'Введите имя'
        }
        if (!values.index) {
            errors.index = 'Введите фамилию'
        }
        if (!values.coefficient) {
            errors.coefficient = 'Введите возраст'
        }
        if (!values.month) {
            errors.month = 'Введите адрес электронной почты'
        }

        return errors
    }

    getUsers() {
        UserDataService.retrieveAllUsers()
            .then(
                response => {
                    console.log(response.data);
                    this.setState({ users: response.data })
                })
    }

    getUser(id){
        UserDataService.retrieveUser(id)
            .then(response => {
                return response.data;
            })
    }

    handleDropdownChange(e) {
        this.setState({ user: e.target.value });
      }


    render() {
        let { percent, index, coefficient, month, user, id } = this.state

        return (
            <div>
                <h3>User</h3>
                <div className="container">
                    <Formik
                        initialValues={{ percent, index, coefficient, month, user, id }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="percent" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="index" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="coefficient" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="month" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Id</label>
                                        <Field className="form-control" type="text" name="id" disabled />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>% выполнения задач</label>
                                        <Field className="form-control" type="text" name="percent" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Оценка</label>
                                        <Field className="form-control" type="text" name="index" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Коэф-т</label>
                                        <Field className="form-control" type="text" name="coefficient" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Месяц</label>
                                        <Field className="form-control" type="text" name="month" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Сотрудник</label>
                                        <select
                                            className="form-control"
                                            name="user"
                                            value={this.props.user}
                                            onChange={this.handleDropdownChange}
                                        >
                                            {
                                                this.state.users.map(user => (
                                                    <option key={user.id} value={user.id}>
                                                        {user.name} {user.surname}
                                                    </option>
                                                ))
                                            }
                                        <option value="green" label="green" />
                                        </select>
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Сохранить</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }

}

export default WorkAmountComponent
