import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import UserDataService from '../service/UserDataService';

class UserDetailsComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            name: '',
            surname: '',
            age: '',
            email: ''
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
    }

    componentDidMount() {

        console.log(this.state.id)

        UserDataService.retrieveUser(this.state.id)
            .then(response => this.setState({
                name: response.data.name,
                surname: response.data.surname,
                age: response.data.age,
                email: response.data.email
            }))

    }

    onSubmit(values) {
        let user = {
            id: this.state.id,
            name: values.name,
            surname: values.surname,
            age: values.age,
            email: values.email
        }


        if (values.id == -1) {
            UserDataService.createUser(user)
                .then(() => this.props.history.push('/users'))
        } else {
            UserDataService.updateUser(this.state.id, user)
                .then(() => this.props.history.push('/users'))
        }

        console.log(values);
    }

    validate(values) {
        let errors = {}
        if (!values.name) {
            errors.name = 'Введите имя'
        } else if (values.name.length < 2) {
            errors.name = 'Имя должно состоять минимум из 2-х букв'
        }
        if (!values.surname) {
            errors.surname = 'Введите фамилию'
        } else if (values.surname.length < 3) {
            errors.surname = 'Фамилия должно состоять минимум из 2-х букв'
        }
        if (!values.age) {
            errors.age = 'Введите возраст'
        }
        if (!values.email) {
            errors.email = 'Введите адрес электронной почты'
        } else if (values.email.length < 8) {
            errors.email = 'Имя должно состоять минимум из 8-ми символов'
        }

        return errors
    }

    render() {
        let { name, surname, id, age, email } = this.state

        return (
            <div>
                <h3>Пользователь</h3>
                <header class="header">
                    <div class="container">
                        <div class="teacher-name" style="padding-top:20px;">

                            <div class="row" >
                                <div class="col-md-9">
                                    <h2 ><strong>{this.state.name} {this.state.surname}</strong></h2>
                                </div>
                                <div class="col-md-3">
                                    <div class="button" >
                                        <a href="#" class="btn btn-outline-success btn-sm">Edit Profile</a>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div class="row" >
                            <div class="col-md-3">
                                <a href="#"> <img class="rounded-circle" src="../resources/getimg.png" alt="Kamal"  /></a>
                            </div>

                            <div class="col-md-6">
                                <h5 style="color:#3AAA64">Associate Professor, <small>Dept. of CSE, Jatiya Kabi Kazi Nazrul Islam University</small></h5>
                                <p>PhD (On study at BUET), M.Sc. in research on ICT(UPC, Spain), M.Sc. in research on ICT(UCL, Belgium).</p>
                                <p>Address: Namapara, Trishal, Mymensingh</p>
                            </div>

                            <div class="col-md-3 text-center">
                                <span class="number" style="font-size:18px">Phone:<strong>+8801732226402</strong></span>
                                <div class="button" style="padding-top:18px">
                                    <a href="mailto:ahmkctg@yahoo.com" class="btn btn-outline-success btn-block">Send Email</a>
                                </div>
                                <div class="social-icons" style="padding-top:18px">
                                    <a href="#">
                                        <span class="fa-stack fa-lg">
                                            <i class="fa fa-circle fa-stack-2x" style="color:#3AAA64"></i>
                                            <i class="fa fa-linkedin fa-stack-1x fa-inverse"></i>
                                        </span></a>
                                    <a href="#">
                                        <span class="fa-stack fa-lg">
                                            <i class="fa fa-circle fa-stack-2x" style="color:#3AAA64"></i>
                                            <i class="fa fa-google-plus fa-stack-1x fa-inverse"></i>
                                        </span></a>
                                    <a href="#">
                                        <span class="fa-stack fa-lg">
                                            <i class="fa fa-circle fa-stack-2x" style="color:#3AAA64"></i>
                                            <i class="fa fa-facebook fa-stack-1x fa-inverse"></i>
                                        </span></a>
                                    <a href="#">
                                        <span class="fa-stack fa-lg">
                                            <i class="fa fa-circle fa-stack-2x" style="color:#3AAA64"></i>
                                            <i class="fa fa-slideshare fa-stack-1x fa-inverse"></i>
                                        </span></a>

                                </div>
                            </div>
                        </div>
                    </div>
                </header>
                <Formik
                    initialValues={{ id, name, surname, age, email }}
                    onSubmit={this.onSubmit}
                    validateOnChange={false}
                    validateOnBlur={false}
                    validate={this.validate}
                    enableReinitialize={true}
                >
                    {
                        (props) => (
                            <Form>
                                <ErrorMessage name="name" component="div"
                                    className="alert alert-warning" />
                                <ErrorMessage name="surname" component="div"
                                    className="alert alert-warning" />
                                <ErrorMessage name="age" component="div"
                                    className="alert alert-warning" />
                                <ErrorMessage name="email" component="div"
                                    className="alert alert-warning" />
                                <fieldset className="form-group">
                                    <label>Id</label>
                                    <Field className="form-control" type="text" name="id" disabled />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>Имя</label>
                                    <Field className="form-control" type="text" name="name" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>Фамилия</label>
                                    <Field className="form-control" type="text" name="surname" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>Возраст</label>
                                    <Field className="form-control" type="number" name="age" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>E-mail</label>
                                    <Field className="form-control" type="email" name="email" />
                                </fieldset>
                                <button className="btn btn-success" type="submit">Сохранить</button>
                            </Form>
                        )
                    }
                </Formik>
            </div>
        )
    }
}

export default UserDetailsComponent
