import React, { Component } from 'react';
// import PopPop from './node_modules/react-poppop/lib/index';
const PopPop = require('react-poppop');

export default class AllowancePopFormComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      show: false
    }

    this.onSubmit = this.onSubmit.bind(this)
    this.validate = this.validate.bind(this)
    this.handleStatusDropdownChange = this.handleStatusDropdownChange.bind(this)
    this.handleEducationDropdownChange = this.handleEducationDropdownChange.bind(this)
    this.handleEducationPlaceDropdownChange = this.handleEducationPlaceDropdownChange.bind(this)
  }

  toggleShow = show => {
    this.setState({ show });
  }

  render() {
    const { show } = this.state;
    return (
      <div>
        <button className="btn btn-default" onClick={() => this.toggleShow(true)}>Show Modal</button>
        <PopPop position="centerCenter"
          open={show}
          closeBtn={true}
          closeOnEsc={true}
          onClose={() => this.toggleShow(false)}
          closeOnOverlay={true}>
          <h1>title</h1>
          <p>
            content
          </p>
        </PopPop>
      </div>
    )
  }
}
