import React, { Component } from 'react';
import UserDataService from '../service/UserDataService';
import BonusDataService from '../service/BonusService';

class ListBonusesComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            bonuses: [],
            message: null
        }
        this.refreshBonuses = this.refreshBonuses.bind(this)
        this.deleteBonusClicked = this.deleteBonusClicked.bind(this)
        this.updateBonusClicked = this.updateBonusClicked.bind(this)
        this.addBonusClicked = this.addBonusClicked.bind(this)
    }

    componentDidMount() {
        this.refreshSalaries();
    }

    refreshBonuses() {
        BonusDataService.retr
            .then(
                response => {
                    console.log(response);
                    this.setState({ salaries: response.data })
                }
            )
    }

    deleteSalaryClicked(id) {
        var result = window.confirm("Вы действительно хотите удалить данную информацию?");
        SalaryService.deleteSalary(id)
            .then(
                response => {
                    this.setState({ message: `Delete of info ${id} Successful` })
                    this.refreshSalaries()
                }
            )
    }

    updateSalaryClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/salaries/${id}`)
    }

    addSalaryClicked() {
        this.props.history.push(`/salaries/-1`)
    }

    getUserName(id) {
        UserDataService.retrieveUser(id)
            .then(response => {
                return response.data.name + ' ' + response.data.surname;
            }
            )
    }

    render() {
        return (
            <div className="container">
                <h3>Информация о зарплатах сотрудников</h3>
                {this.state.message && <div className="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead className="thead-dark">
                            <tr>
                                <th>Сотрудник</th>
                                <th>Тип</th>
                                <th>Оклад</th>
                                <th>Премия</th>
                                <th>Суммарно</th>
                                <th>Месяц</th>
                                <th>Удалить</th>
                                {/* <th>Редактировать</th> */}
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.salaries.map(
                                    salary =>
                                        <tr key={salary.id}>
                                            <td>{salary.user.name} {salary.user.surname}</td>
                                            <td>{salary.type}</td>
                                            <td>{salary.salary}</td>
                                            <td>{salary.premiya}</td>
                                            <td>{salary.sum}</td>
                                            <td>{salary.month}</td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteSalaryClicked(salary.id)}>Удалить</button></td>
                                            {/* <td><button className="btn btn-success" onClick={() => this.updateUserClicked(user.id)}>Редактировать</button></td> */}
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addSalaryClicked}>Добавить</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default ListSalariesComponent
