import React, { Component } from 'react'
import Chart from 'chart.js';

class DiagramComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
        
        }

        this.drawDiagram = this.drawDiagram.bind(this)
    }

    componentDidMount() {

    this.drawDiagram();
    }

    drawDiagram(){
        var ctx = document.getElementById('myChart');
        var myChart = new Chart(ctx, {
            type: 'bar',
            data: {
                labels: ['Апрель', 'Июнь', 'Май'],
                datasets: [{
                    label: 'Оценка',
                    data: [12, 19, 3, 5, 2, 3],
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)'
                    ],
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    yAxes: [{
                        ticks: {
                            beginAtZero: true
                        }
                    }]
                }
            }
        });
    }

    render() {
        return (
            <canvas id="myChart" width="400" height="400"></canvas>
        )
    }
}

export default DiagramComponent
