import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import WorkAmountDataService from '../service/WorkAmountDataService';
import UserDataService from '../service/UserDataService';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

class WorkAmountComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            workdays: '',
            totalWorkdays: '',
            assessment: '',
            month: '',
            year: '',
            user: {
                id: ''
            },
            users: []
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
        this.handleDropdownChange = this.handleDropdownChange.bind(this)
        this.handleMonthDropdownChange = this.handleMonthDropdownChange.bind(this)
    }

    componentDidMount() {

        console.log(this.state.id)

        this.getUsers();

        // eslint-disable-next-line
        if (this.state.id == -1) {
            return
        }

        WorkAmountDataService.retrieveAmount(this.state.id)
            .then(response => this.setState({
                workdays: response.data.workdays,
                totalWorkdays: response.data.totalWorkdays,
                assessment: response.data.assessment,
                month: response.data.month,
                year: response.data.year,
                user: {
                    id: response.data.user.id
                }
            }))

    }

    onSubmit(values) {
        // let setUser = this.getUser(values.user);
        let workAmount = {
            id: this.state.id,
            workdays: values.workdays,
            totalWorkdays: values.totalWorkdays,
            assessment: values.assessment,
            month: values.month,
            year: values.year,
            user: {
                id: values.user
            }
        }


        if (values.id == -1) {
            console.log(workAmount);
            WorkAmountDataService.createAmount(workAmount)
                .then(() => this.props.history.push('/workamounts'))
        } else {
            WorkAmountDataService.updateAmount(this.state.id, workAmount)
                .then(() => this.props.history.push('/workamounts'))
        }

        console.log(values);
    }

    validate(values) {
        let errors = {}
        if (!values.percent) {
            errors.percent = 'Введите имя'
        }
        if (!values.index) {
            errors.index = 'Введите фамилию'
        }
        if (!values.coefficient) {
            errors.coefficient = 'Введите возраст'
        }
        if (!values.month) {
            errors.month = 'Введите адрес электронной почты'
        }
        if (values.user.id === "") {
            errors.user = 'Выберите сотрудника перед сохранением'
        }

        return errors
    }

    getUsers() {
        UserDataService.retrieveAllUsers()
            .then(
                response => {
                    console.log(response.data);
                    this.setState({ users: response.data })
                })
    }

    getUser(id) {
        UserDataService.retrieveUser(id)
            .then(response => {
                return response.data;
            })
    }

    handleDropdownChange(e) {
        this.setState({ user: e.target.value });
    }

    handleMonthDropdownChange(e) {
        this.setState({ month: e.target.value });
    }


    render() {
        let { workdays, totalWorkdays, assessment, month, year, user, id } = this.state

        return (
            <div>
                <h3>User</h3>
                <div className="container">
                    <Formik
                        initialValues={{ workdays, totalWorkdays, assessment, month, year, user, id }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="percent" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="index" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="coefficient" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="month" component="div"
                                        className="alert alert-warning" />
                                    <ErrorMessage name="user" component="div"
                                        className="alert alert-warning" />
                                    <fieldset className="form-group">
                                        <label>Id</label>
                                        <Field className="form-control" type="text" name="id" disabled />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Дней отработано(в мес.)</label>
                                        <Field className="form-control" type="number" name="workdays" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Всего рабочих дней(в мес.)</label>
                                        <Field className="form-control" type="number" name="totalWorkdays" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Оценка(1-5)</label>
                                        <Field className="form-control" min="1" max="5" type="number" name="assessment" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Месяц</label>
                                        <select
                                            labelId="demo-simple-select-label"
                                            id="demo-simple-select"
                                            value={this.props.month}
                                            onChange={this.handleMonthDropdownChange}
                                        >
                                            <MenuItem value={"Январь"}>Январь</MenuItem>
                                            <MenuItem value={"Февраль"}>Февраль</MenuItem>
                                            <MenuItem value={"Март"}>Март</MenuItem>
                                            <MenuItem value={"Апрель"}>Апрель</MenuItem>
                                            <MenuItem value={"Май"}>Май</MenuItem>
                                            <MenuItem value={"Июнь"}>Июнь</MenuItem>
                                            <MenuItem value={"Июль"}>Июль</MenuItem>
                                            <MenuItem value={"Август"}>Август</MenuItem>
                                            <MenuItem value={"Сентябрь"}>Сентябрь</MenuItem>
                                            <MenuItem value={"Октябрь"}>Октябрь</MenuItem>
                                            <MenuItem value={"Ноябрь"}>Ноябрь</MenuItem>
                                            <MenuItem value={"Декабрь"}>Декабрь</MenuItem>
                                        </select>
                                        <label>Год</label>
                                        <Field className="form-control" type="number" name="year" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Сотрудник</label>
                                        <select
                                            className="form-control"
                                            name="user"
                                            value={this.props.user}
                                            onChange={this.handleDropdownChange}
                                        >
                                            {
                                                this.state.users.map(user => (
                                                    <option key={user.id} value={user.id}>
                                                        {user.name} {user.surname}
                                                    </option>
                                                ))
                                            }
                                            <option value='0' label="Выберите сотрудника" />
                                        </select>
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Сохранить</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }

}

export default WorkAmountComponent
